/*Table structure for table `goods_base` */

DROP TABLE IF EXISTS `goods_base`;

CREATE TABLE `goods_base` (
  `id` bigint(20) NOT NULL COMMENT '商品id',
  `goods_status` int(1) NOT NULL DEFAULT '1' COMMENT '0下架1正常2已删除',
  `price` bigint(20) NOT NULL COMMENT '价格(单位是分)',
  `stock` int(11) NOT NULL COMMENT '剩余库存数目',
  `category` int(11) NOT NULL COMMENT '商品分类',
  `goods_version` bigint(20) NOT NULL DEFAULT '0' COMMENT '商品版本',
  `goods_buy_version` bigint(20) NOT NULL DEFAULT '0' COMMENT '商品购买版本',
  `create_time` datetime NOT NULL COMMENT '添加时间',
  `create_user_id` bigint(20) NOT NULL COMMENT '添加者',
  `update_time` datetime NOT NULL COMMENT '修改时间',
  `update_user_id` bigint(20) NOT NULL COMMENT '修改者',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

