/*Table structure for table `user` */

DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '用户id,自增',
  `loginname` varchar(20) NOT NULL COMMENT '登录名',
  `password` varchar(100) NOT NULL COMMENT '密码',
  `realname` varchar(20) NOT NULL COMMENT '真实姓名',
  `sex` int(1) NOT NULL COMMENT '性别:1男0女',
  `birthday` date NOT NULL COMMENT '用户生日日期',
  `phone` varchar(20) NOT NULL COMMENT '手机号',
  `email` varchar(50) NOT NULL COMMENT '邮箱',
  `pwd_error_count` int(1) NOT NULL DEFAULT '0' COMMENT '当日输错密码次数',
  `is_locked` int(1) NOT NULL DEFAULT '0' COMMENT '是否锁定:1是0否',
  `lock_time` datetime DEFAULT NULL COMMENT '上次锁定时间',
  `user_type` int(11) NOT NULL COMMENT '用户类型:0顾客1后台管理员',
  `last_login_ip` varchar(50) NOT NULL COMMENT '最后登录的ip地址',
  `last_login_time` datetime NOT NULL COMMENT '最后登录时间',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `last_update_time` datetime NOT NULL COMMENT '最后更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `loginname` (`loginname`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

